(1741, 'Introducing Whisper', 'We’ve trained and are open-sourcing a neural net called Whisper that approaches human level robustness and accuracy on English speech recognition.', 1663743600, 6, 'https://openai.com/research/whisper', 1713122732),
(1742, 'Efficient training of language models to fill in the middle', '', 1658991600, 6, 'https://openai.com/research/efficient-training-of-language-models-to-fill-in-the-middle', 1713122732),
